    dataSource.setJdbcUrl("jdbc:mysql://120.24.90.60:3306/mybatis_study?useSSL=false&characterEncoding=UTF-8");
    dataSource.setUser("root");
    dataSource.setPassword("12345678");
    // 设置连接池信息
    // 最大连接数
    dataSource.setMaxPoolSize(50);
    // 初始连接数
